/*Table structure for table `wp_posts` */

DROP TABLE IF EXISTS `wp_posts`;

CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=175 DEFAULT CHARSET=utf8;

/*Data for the table `wp_posts` */

insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2013-10-02 09:55:26','2013-10-02 09:55:26','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2013-10-02 09:55:26','2013-10-02 09:55:26','',0,'http://mamnon.dev/?p=1',0,'post','',1),(2,1,'2013-10-02 09:55:26','2013-10-02 09:55:26','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://mamnon.dev/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','open','open','','sample-page','','','2013-10-02 09:55:26','2013-10-02 09:55:26','',0,'http://mamnon.dev/?page_id=2',0,'page','',0),(4,1,'2013-10-03 02:46:30','0000-00-00 00:00:00','','Home','','draft','open','open','','','','','2013-10-03 02:46:30','0000-00-00 00:00:00','',0,'http://mamnon.dev/?p=4',1,'nav_menu_item','',0),(5,1,'2013-10-03 02:46:30','0000-00-00 00:00:00',' ','','','draft','open','open','','','','','2013-10-03 02:46:30','0000-00-00 00:00:00','',0,'http://mamnon.dev/?p=5',1,'nav_menu_item','',0),(6,1,'2013-10-03 03:02:56','2013-10-03 03:02:56','<h3 style=\"text-align: center;\">Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\r\n<div><img class=\"aligncenter\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\r\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\r\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\r\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\r\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\r\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\r\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\r\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\r\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\r\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\r\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\r\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\r\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\r\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\r\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','publish','open','open','','gioi-thieu-chung','','','2013-10-03 03:07:50','2013-10-03 03:07:50','',0,'http://mamnon.dev/?page_id=6',0,'page','',0),(7,1,'2013-10-03 03:02:56','2013-10-03 03:02:56','<h3>Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\r\n<div><img class=\"aligncenter\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\r\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\r\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\r\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\r\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\r\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\r\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\r\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\r\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\r\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\r\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\r\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\r\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\r\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\r\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','inherit','open','open','','6-revision-v1','','','2013-10-03 03:02:56','2013-10-03 03:02:56','',6,'http://mamnon.dev/?p=7',0,'revision','',0),(8,1,'2013-10-03 03:04:00','2013-10-03 03:04:00','<h3 style=\"text-align: center;\">Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\n<div><img class=\"aligncenter\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','inherit','open','open','','6-autosave-v1','','','2013-10-03 03:04:00','2013-10-03 03:04:00','',6,'http://mamnon.dev/?p=8',0,'revision','',0),(9,1,'2013-10-03 03:04:12','2013-10-03 03:04:12','<h3 style=\"text-align: center;\">Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\r\n<div><img class=\"aligncenter\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\r\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\r\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\r\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\r\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\r\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\r\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\r\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\r\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\r\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\r\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\r\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\r\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\r\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\r\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','inherit','open','open','','6-revision-v1','','','2013-10-03 03:04:12','2013-10-03 03:04:12','',6,'http://mamnon.dev/?p=9',0,'revision','',0),(10,1,'2013-10-03 03:07:23','2013-10-03 03:07:23','<h3 style=\"text-align: center;\">Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\r\n<div><img class=\"alignnone\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\r\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\r\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\r\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\r\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\r\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\r\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\r\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\r\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\r\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\r\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\r\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\r\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\r\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\r\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','inherit','open','open','','6-revision-v1','','','2013-10-03 03:07:23','2013-10-03 03:07:23','',6,'http://mamnon.dev/?p=10',0,'revision','',0),(11,1,'2013-10-03 03:07:50','2013-10-03 03:07:50','<h3 style=\"text-align: center;\">Giới thiệu trường Mầm non Chất lượng cao Thăng Long KidsMart</h3>\r\n<div><img class=\"aligncenter\" alt=\"Trường Mầm non Thăng Long Kidsmart\" src=\"http://mamnonlongbien.com/uploads/about/thanglongkidsmat.jpg\" width=\"506\" height=\"367\" /></div>\r\nTrường Mầm non chất lượng cao Thăng Long KidsMart tọa lạc tại Số 37 Ngõ 67 Đức Giang  -  Quận Long Biên - Thành phố Hà Nội. Trường được thành lập vào tháng 9/2009 nhằm đáp ứng nhu cầu chung của xã hội về một môi trường giáo dục Mầm non an toàn, tin cậy, hiệu quả trong việc hình thành nhân cách, nếp sống văn hóa và nếp học tập cho trẻ ngay từ tuổi nhỏ. Với đội ngũ giáo viên có trình độ, giàu kinh nghiệm trong giảng dạy, thật sự tâm huyết với nghề ươm mầm xanh, luôn yêu thương, quý mến trẻ . Mầm non chất lượng cao Thăng Long KidsMart cam kết mang lại cho trẻ một môi trường sư phạm thực sự tin cậy, hiệu quả .\r\nĐến với Mầm non chất lượng cao Thăng Long KidsMart, trẻ được nuôi dạy theo đúng chương trình chăm sóc giáo dục mầm non của Bộ GD&amp;ĐT. Ngoài ra, với chương trình học linh hoạt, trẻ còn được tạo điều kiện phát triển các năng khiếu của mình một cách tự nhiên, tạo cho trẻ niềm đam mê và hứng thú học tập ngay từ khi còn nhỏ. Nhà trường đặc biệt chú trọng phương châm lấy trẻ làm trung tâm trong mọi chương trình và hoạt động của trường, vì vậy trong mọi việc từ rèn luyện nề nếp ăn, ngủ tới việc học tập, nhà trường đều căn cứ vào sự phát triển của từng trẻ để có phương pháp đưa dần các cháu vào nếp sinh hoạt và học tập chung của trường một cách hài hòa nhất, phù hợp với đặc điểm tâm sinh lý của các cháu sao cho phát huy được tính sáng tạo, ham học hỏi ở trẻ một cách tốt nhất .Trẻ mầm non cần được “học mà chơi, chơi mà học” ở đây con bạn sẽ được học trong khi chơi, được làm quen với một số kiến thức sơ đẳng một cách tự nhiên, từ từ, có hệ thống theo chương trình quy định của Bộ GD&amp;ĐT.  Đặc biệt các con chuẩn bị vào lớp một, chúng tôi có chương trình giáo dục đặc biệt <em>‘‘Chuẩn bị tâm thế tốt cho trẻ vào lớp một’’</em>.\r\nCác bé mẫu giáo được học thêm các môn năng khiếu nhằm khai thác tối đa những khả năng tiềm ẩn trong con người bé (vẽ, võ, tiếng anh, vi tính, múa).\r\nVới diện tích hơn 3.000 m2 , trường gồm 2 tòa nhà 3 tầng, 13 phòng học lớn ( diện tích phòng học 90 - 100 mét vuông) và 15 phòng chức năng được thiết kế đúng tiêu chuẩn theo nguyên lý lấy học sinh làm trung tâm. Trường có hệ thống sân vườn rộng, thoáng đẹp, trang trí hệ thống cây xanh đảm bảo mỹ thuật, sinh động, khoa học phù hợp với thị hiếu của trẻ . Trong mỗi lớp học tạo cho trẻ luôn có cảm giác chơi mà học, học mà chơi. Ánh sáng chan hoà, không khí thoáng mát cùng thiên nhiên là tiêu chí quan trọng mà mỗi lớp học đạt được. Đây chính là môi trường lý tưởng cho trẻ học tập, vui chơi phát triển trí tụê, năng khiếu.\r\nVới không gian các phòng học rộng, thoáng mát, được trang bị điều hòa hai chiều cùng các đồ dùng, đồ chơi được sắp xếp trưng bày góc theo từng chủ đề, chủ điểm, theo các mùa một cách sinh động và hấp dẫn, trẻ sẽ được tạo điều kiện tốt nhất để học tập và phát huy các khả năng của mình. Ngoài các phòng học nhà trường còn có đủ các phòng chức năng và phòng năng khiếu hỗ trợ tích cực cho việc học tập các môn năng khiếu ở trường của trẻ.\r\nBên cạnh chương trình tiếng Việt, hàng tuần các bé sẽ được  làm quen với tiếng Anh do người nước ngoài giảng dạy. Học ngoại ngữ từ nhỏ rất tốt cho quá trình phát triển tư duy ngôn ngữ của trẻ, với chương trình học bài bản cùng giáo viên người Việt có trình độ và giáo viên bản ngữ giàu kinh nghiệm, Mầm non chất lượng cao Thăng Long KidsMart hi vọng tạo dựng được nền tảng ngôn ngữ vững chắc, cho trẻ tự tin sử dụng tiếng Anh trong quá trình học tập sau này.\r\nĐể đảm bảo việc giáo dục đạt kết quả toàn diện, tại trường trẻ được nuôi dưỡng theo đúng khoa học từ việc an toàn vệ sinh thực phẩm đến khâu chế biến. Hệ thống bếp một chiều với đầy đủ các dụng cụ nhà bếp theo đúng tiêu chuẩn quy định, các suất ăn của trẻ cũng được nhà trường nghiên cứu cẩn thận, khoa học nhằm đảm bảo chế độ dinh dưỡng cho trẻ . Ở Mầm non Thăng Long KidsMart, các con được ăn theo các chế độ khác nhau, phù hợp với từng lứa tuổi và nhu cầu của các con. Để đảm bảo nhu cầu dinh dưỡng cho các con, mỗi ngày các con được ăn 05 bữa (ăn sáng 7h30’ + phụ, uống sữa  9h + ăn trưa 10h30 + ăn chiều 14h30 + uống sữa 16h) chiếm 70% - 75% năng lượng trong ngày của bé. Đặc biệt, thực hiện nghiêm ngặt chế độ dinh duỡng sạch cho trẻ với rau sạch, thịt sạch cho các bữa ăn, nước uống  được đảm bảo tiệt trùng  nóng và lạnh.Với hệ thống nước nóng bằng bình nóng lạnh điện các con được đảm bảo sử dụng nước ấm thường xuyên kể cả mùa hè. Mùa hè nóng bức bạn lo lắng đến giấc ngủ của con, chúng tôi có hệ thống điều hòa hai chiều nếu sức khỏe của con bạn thích ứng với nó .\r\nCon mới đi học bạn lo lắng, không biết con có ngoan không, đến thăm con bạn sợ con theo. Đừng ngần ngại, bạn có thể đến thăm con bất kỳ lúc nào. Chúng tôi có hệ thống camera quan sát đặt ở tầng một. Qu‎ý vị có thể  thấy con mình mà không sợ cháu theo.Hướng tới mục tiêu phụ huynh và nhà trường cùng chăm sóc giáo dục trẻ, tại các phòng học chúng tôi đã lắp đặt camera để các bậc phụ huynh có thể theo dõi trực tiếp mọi sinh hoạt của trẻ ở trường khi vào website của trường.\r\nĐến Mầm non chất lượng cao Thăng Long KidsMart, mọi đồ dùng cá nhân của bé như: giường, gối, chăn, ga, ca uống nước, khăn mặt….đều được dùng riêng cho từng trẻ, hàng ngày được giặt sạch, sát trùng.\r\nTất cả các hoạt động của nhà trường đều được kiểm tra một cách chặt chẽ, chu đáo đến từng chi tiết nhỏ. Đây chính là nét mới trong chất lượng đầu tư của một trường Tư thục .\r\nVới tất cả những giá trị thiết thực mà nhà trường mang đến cho học sinh với mức học phí vừa phải, trường Mầm non chất lượng cao Thăng Long KidsMart là địa chỉ lý tưởng cho tương lai học vấn của thiên thần nhà bạn .\r\nMầm non chất lượng cao Thăng Long KidsMart mong muốn tạo dựng cho trẻ môi trường học tập tốt nhất, hỗ trợ tối đa cho việc hình thành nhân cách, hình thành nếp sống, nếp sinh hoạt khoa học cho trẻ, vì vậy các bậc phụ huynh có thể tin tưởng giao con cho các cô chăm sóc và dạy dỗ.\r\nVới phương châm <em>‘‘Mỗi ngày đến lớp là một ngày vui’’</em>, dành cho con trẻ những gì yêu thương nhất.\r\nChúng tôi xin được mạnh dạn gửi tới gia đình một địa chỉ mầm non tin cậy, hy vọng rằng ở đây quý vị có thể hoàn toàn yên tâm về sự phát triển của con mình.','Giới thiệu chung','','inherit','open','open','','6-revision-v1','','','2013-10-03 03:07:50','2013-10-03 03:07:50','',6,'http://mamnon.dev/?p=11',0,'revision','',0),(12,1,'2013-10