insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (164,1,'2013-10-09 09:54:05','2013-10-09 09:54:05','<div class=\"short-desc clearfix\" id=\"hometext\" style=\"margin: 0px; padding: 8px 0px; border-width: 0px 0px 1px; border-bottom-style: dashed; border-bottom-color: #dcdcdc; outline: 0px; font-size: 12px; vertical-align: baseline; background-color: #ffffff; color: #4d4d4d; font-family: Verdana, Arial, Helvetica, sans-serif; line-height: 16px;\"><span itemprop=\"description\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;\">Thực hiện nhiệm vụ năm học 2013 - 2014 về việc chăm sóc sức khỏe cho trẻ, ngày 30 tháng 09 năm 2013 trường Mầm non Thăng Long Kidsmart phối hợp với Trạm y tế phường Đức Giang tổ chức khám sức khỏe toàn diện cho toàn thể học sinh.</span></div>\r\n<div class=\"details-content clearfix\" id=\"bodytext\" style=\"margin: 0px; padding: 8px 0px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; background-color: #ffffff; color: #000000; font-family: Tahoma, Arial, Helvetica, sans-serif; line-height: 16px;\">Chất lượng <a style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; color: #ff6600;\" href=\"http://mamnonlongbien.com/news/cham-soc-tre.html\">chăm sóc sức khỏe</a> và nuôi dưỡng trẻ được quan tâm hàng đầu tại trường Mầm nonThăng Long Kidsmart Long Biên. Vì vậy, mỗi năm nhà trường tổ chức 4 đợt khám sức khỏe toàn diện cho trẻ. Qua những đợt khám sức khỏe này giúp nhà trường kịp thời phát hiện những cháu bị các bệnh thông thường, có nguy cơ béo phì, suy dinh dưỡng để cùng phối hợp với gia đình có <a style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; color: #ff6600;\" href=\"http://mamnonlongbien.com/news/dinh-duong-cho-tre-em.html\">chế độ chăm sóc</a> và chữa bệnh thích hợp. Sau đây là một số hình ảnh của buổi khám sức khỏe tại trường:\r\n<ul style=\"margin: 0px 1.5em 1.5em; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; list-style-position: initial; list-style-image: initial;\">\r\n	<li style=\"margin: 0px; padding: 5px 0px 5px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-image: url(\'http://mamnonlongbien.com/themes/kid/images/bulet-flower.png\'); list-style: none; background-position: 2px 7px; background-repeat: no-repeat no-repeat;\">Các cháu được kiểm tra họng rất kỹ: Do độ tuổi của các cháu và <a style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-decoration: none; color: #ff6600;\" href=\"http://mamnonlongbien.com/news/phu-huynh/phong-benh-thuong-gap-trong-mua-thu-cho-be-272.html\">thời điểm giao mùa</a> nên các cháu hay bị các bệnh viêm họng, viêm đường hô hấp nên cần được kiểm tra kỹ đường hô hấp trên</li>\r\n</ul>\r\n<ul style=\"margin: 0px 1.5em 1.5em; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; list-style-position: initial; list-style-image: initial;\">\r\n	<li style=\"margin: 0px; padding: 5px 0px 5px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-image: url(\'http://mamnonlongbien.com/themes/kid/images/bulet-flower.png\'); list-style: none; background-position: 2px 7px; background-repeat: no-repeat no-repeat;\">Ngoài ra các bé được cân, đo để xem các bé có phát triển tốt không.</li>\r\n	<li style=\"margin: 0px; padding: 5px 0px 5px 20px; border: 0px; outline: 0px; vertical-align: baseline; background-image: url(\'http://mamnonlongbien.com/themes/kid/images/bulet-flower.png\'); list-style: none; background-position: 2px 7px; background-repeat: no-repeat no-repeat;\">Kết quả kiểm tra sức khoẻ của trẻ đã được cán bộ y tế ghi chép cụ thể trong sổ theo dõi sức khỏe để nhà trường và phụ huynh tiện theo dõi.</li>\r\n</ul>\r\n<div style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-align: center;\"><img style=\"margin: 0px; padding: 0px; border-width: 3px; border-style: solid; outline: 0px; vertical-align: baseline; background-color: transparent; width: 640px; height: 480px;\" alt=\"\" src=\"http://mamnonlongbien.com/uploads/news/hoat-dong-truong/2013/10/kham-suc-khoe-dinh-ky-2.jpg\" /></div>\r\n</div>','Khám sức khỏe đầu năm cho các bé năm học 2013 - 2014','','publish','open','open','','kham-suc-khoe-dau-nam-cho-cac-be-nam-hoc-2013-2014','','','2013-11-06 10:47:34','2013-11-06 10:47:34','',0,'http://mamnon.dev/?p=164',0,'post','',0);
